css:
@media screen {
    .print-format {
        padding: 0in;
        margin: 0in;
    }
}
.d-flex {
    display: flex !important;
    display: -webkit-box;
}
.flex-wrap {
    flex-wrap: wrap!important;
}
.id-card {
    font-family: 'Made Tommy', sans-serif;
    position: relative;
    height:3.37in;
    width:2.125in;
    margin-bottom:0.25in;
    margin-left: 0.2in;
    break-inside: avoid;
}
.id-card1 {
    font-size:8px;
    position:relative;
    height:3.37in;
    width:2.125in;
    margin-bottom:0.25in;
    margin-left: 0.2in;
    break-inside: avoid;
}
.id-card-back-image {
 position: absolute;
 width: 100%;
 height: 100%;
 z-index: 0;
}

.back-image {
 position: absolute;
 width: 100%;
 height: 100%;
 z-index: 0;
}

.cross-bar {
 width: 100%;
   height: 100%;
   position: absolute;
 height: 1.157in;
 background-color: cadetblue;
 z-index: 10;
 top: 1.157in;
 transform: skewY(-33deg);
 -webkit-transform: skewY(-33deg);
}

.photo-name {
 height: 1.1in;
 width: 1.37in;
 position: absolute;
 z-index: 11;
 top: 0.707in;
 right: 0.217in;
}
.photo {
 height: 0.813in;
 width: 0.813in;
 background-color: white;
 border: 0.013in solid black;
 float: right;
 margin-left: 0.6in;
}

.name {
 float: right;
 font-size: 9px;
 font-weight: bold;
 text-align: right;
}

.cross-bar-inner {
    position: absolute;
    transform: skewY(33deg);
    -webkit-transform: skewY(33deg);
}

.vert-txt {
 position: absolute;
 transform: rotate(-90deg);
 -webkit-transform: rotate(-90deg);
 text-transform: uppercase;
 height: 0.3in;
 width: 1.29in;
 text-align: center;
 font-size: 16px;
 font-weight: bold;
 top: 207px;
 left: -48px;
 z-index: 15;
}

.sign {
 padding-left: 8px;
 padding-right: 8px;
 position: absolute;
 bottom: 0.12in;
 z-index: 16;
}

.sign1 {
 font-size: 8px;
 z-index:8;
}

.sign2 {
 font-size: 8px;
 right: 8px;
}

.center {
    margin: auto;
    text-align: center;
}

.info {
 background-color: cadetblue;
 padding-left: 0.1in;
 padding-right: 0.1in;
 padding-top: 0.10in;
 padding-bottom: 0.05in;
 margin-top: 0.45in;
 z-index: 2;
 position: absolute;
 width: 100%;
}

.label1 {
 display: inline;
 padding: 0;
 font-size: 7px;
 font-weight: 700;
 color: inherit;
 white-space: nowrap;
 vertical-align: baseline;
 border-radius: .25em;
}


.addr{
    padding-left:8px;
}

.contact-no {
    padding-left:10px;
    text-align: right;
}

.emergency {
     margin-top:2px; 
    text-align: right;
    padding-top:-10px;
}

.data{
    width:58%;
    float:left;
}

.row{
    padding-bottom:0px;
}
.address{
    position:absolute;
    padding-top:1.98in;
    padding-left:7px;
}

.sep{
    float:left;
}

.inst1 {
    font-size: 6.7px;
    padding: 6px 2px 0px 2px;
    position: absolute;
    top: 1.23in;
    margin-left:-30px;
    background-color:white;
}


.footer {
 width: 100%;
 position: absolute;
 bottom: 3px;
 text-align: center;
}
.dotted-line {
    border: 1px dotted #000; /* You can adjust the width and color */
    width: 100%; /* Adjust according to your layout */
    height: 1; /* Adjust according to your layout */
    position:absolute;
    margin-top:3.37in;
}
.bar_code{
    width: 86px;
    margin-top: 2.2in;
    margin-left: 1.43in;
    position: absolute;
    -webkit-transform: rotate(-90deg);
}
.form-no {
    position: absolute;
    padding-top: -5px; /* Note: negative padding doesn't work, you might want margin */
    z-index: 1;
    font-size: 8px;
    width: 100%;
    display: flex;           /* Make it a flex container */
    justify-content: center; /* Center horizontally */
    align-items: center;     /* Center vertically */
}
